import Layout from "./components/myLayoutBefore";
import React from 'react';
import {Alert} from './components/warnAlert';
import Alert1 from './components/successAlert';
import {Post} from "./components/http";
import "./css/app.css";
import { Privacy } from "./components/Privacy";
import bcrypt from "bcryptjs";

const inputStyle ={
    margin: 20,
    outlineStyle: 'none' ,
    border: '1px solid #ccc',
    borderRadius: '3px',
    padding: '12px 12px',
    width: '70%',
    fontFamily: "Microsoft soft",
    
}
const boxStyle={
    position:'relative',
    left:'20%',
    textAlign:'center',
    width:"60%",
    marginBottom:'20px',
    padding:'30px',
    border:'2px solid  #ffd633',
    borderRadius:'2px',
 
}

const bannerStyle ={
    position:'relative',
    left:'20%',
    textAlign:'center',
    marginTop:"20px",
    width:"60%",
    padding: '10px 10px',
    border:'2px solid  #ffd633',
    backgroundColor:"#ffd633",
    borderRadius:'2px',
   
}

var salt = bcrypt.genSaltSync(10);

function ErrAlert1(props){
    switch(props.errStatus){
        case -1:
            return(
                <div>
                    <Alert title="Warning!" content="Your email has already applicated, please sign in!" />
                </div>
            ) 
        case -2:
            return(
                <div>
                    <Alert title="Warning!" content="Username has already been token, please change!" />
                </div>
            )
        case -3:
            return(
                <div>
                    <Alert title="Warning!" content="Please enter the same password!" />
                </div>
            )
    }
    
}

class accountInfo extends React.Component{
        constructor(props) {
            super(props);
            this.state = {errMsg:'',
                        email:'',
                        psw:'',
                        cfm:'',
                        phone:'',
                        address:'',
                        motivation:'',
                        experience:'',
                        errAlert:'none',
                        successAlert: "none",
                        name: '',
                        showP:'none',
                        errStatus:0,
                        };
            this.pswInput = React.createRef();
            this.cfmInput = React.createRef();
            this.handleChange = this.handleChange.bind(this);
            this.handleSubmit = this.handleSubmit.bind(this);
            this.handleClose = this.handleClose.bind(this);
            this.handleClick = this.handleClick.bind(this);
            this.handleshow = this.handleshow.bind(this)
    }
    handleClick=(e)=>{
        e.preventDefault()
        this.setState({showP:'block'})
    }
    handleshow=()=>{
        this.setState({showP:'none'})
    }
    handleClose(){
        this.setState({
            errAlert: 'none'
        })
    }
        handleChange(event){
            let err = '';
            let name = event.target.name;
            let val = event.target.value;
            let psw = this.pswInput.current.value;
            let cfm = this.cfmInput.current.value;
          
            this.setState({
                [name]:val,
            })
            if(psw !==''&&cfm !==''){
                
                if(psw == cfm){
                    err = ''
                }else{
                    err = 'Different password'
                   
                }
            }
            this.setState({
                psw: this.pswInput.current.value,
                errMsg:err,
            })
           
        }
        handleSubmit(e) {
            e.preventDefault();
            if(this.pswInput.current.value !== this.cfmInput.current.value){
                this.setState({
                    errAlert:'block',
                    errStatus: '-3'
                })
            }
            else{
                var psw = bcrypt.hashSync(this.state.password, salt);
                const data = {
                    email: this.state.email,
                    psw: psw,
                    userName: this.state.userName,
                    name: this.state.name,
                    phone: this.state.phone,
                    address: this.state.address,
                    motivation: this.state.motivation,
                    experience: this.state.experience
                }
                Post('signup',data).then(res=>{
                    console.log(res)
                    if(res == 'true'){
                        this.setState({
                            successAlert: "block"
                        })
                     }else if(res=='-1'){
                        this.setState({
                            errAlert: "block",
                            errStatus: res
                        })
                     }else if(res=='-2'){
                        this.setState({
                            errAlert: "block",
                            errStatus: res
                        })
                     }
                })
                
               
            }
        }
        render(){
            
            return(
                <div className ="container1">
                   <Layout/>
                   <div style={{display: this.state.errAlert}}>
                        <ErrAlert1 errStatus={this.state.errStatus} />
                    </div>
                    <div style={{display: this.state.successAlert}}>
                        <Alert1 title = "Success" content = "Your application has been successfully submitted! Please wait for approving."
                        url="/signin" button="Ok" />
                    </div>

                    <Privacy show={this.state.showP} finish={this.handleshow}/>
                    
                    <div style={bannerStyle}>
                        <span>account info</span>
                    </div> 
                    <form onSubmit={this.handleSubmit}>
                    <div style={boxStyle}>
                        <input style = {inputStyle} type="email" name= "email" 
                        placeholder="Enter your email" value={this.state.value} 
                        onChange={this.handleChange} required="required"/><br/>

                        <input style = {inputStyle} type="password" name = "password" 
                        placeholder="Enter your password"  ref={this.pswInput} value={this.state.value}
                        onChange={this.handleChange} required="required"/><br/>

                        <input style = {inputStyle} type="password" name = "confirm" 
                        placeholder="Enter your password again"  ref={this.cfmInput} 
                        onChange={this.handleChange} required="required"/><span style={{color:'red'}}>{this.state.errMsg}</span><br/>
                        <input style = {inputStyle} type="text" name = "name" 
                        placeholder="Enter your real name" value={this.state.value}
                        onChange={this.handleChange} required="required"/><br/>
                    </div> 
                   
                    <div style={bannerStyle}>
                        <span>Personal info</span>
                    </div> 
                    <div style={boxStyle}>
                        <input style = {inputStyle} type="text" name = "userName" 
                        placeholder="Enter your userName"  value={this.state.value}
                        onChange={this.handleChange} required="required"/><br/>
                    
                        <input style = {inputStyle} type="number" name = "phone" required="required"
                        placeholder="Enter your phone here" value={this.state.value} onChange={this.handleChange}/><br/>

                        <input style = {inputStyle} type="text" name = "address" 
                        placeholder="Enter your address" value={this.state.value}
                        onChange={this.handleChange} required="required"/><br/>
                    </div> 
                    <div style={boxStyle}>
                        <div className="form-group">
                            <textarea style={{marginBottom:'40px',width:'70%',left:'15%',position:'relative'}} className="form-control" name="motivation" onChange={this.handleChange} value={this.state.value} 
                            id="Textarea1" rows="5" placeholder='Enter your motivation' required="required"/>
                        </div>
                        <div className="form-group">
                            <textarea style={{width:'70%',left:'15%',position:'relative'}} className="form-control" name="experience" onChange={this.handleChange} value={this.state.value} 
                            id="Textarea2" rows="5" placeholder='Enter your experience' required="required"/>
                        </div>
                        <div>
                            <input type="checkbox" className="check-input" name="privacy" checked={this.state.value} onChange={this.handleChange} required="required"/>
                            <button onClick={this.handleClick} className="btn-none">
                           
                            I have read the privacy policy statement in this link, and agree with it by selecting this check box.
                            
                            </button>
                        </div>
                        <div style ={{textAlign:'center',marginBottom:'20px'}}>
                            <button type="submit" className="btn btn-primary btn-lg" style={{borderRadius:"4px",marginTop:"20px"}}>Send application</button>
                        </div>
                    </div> 
                </form>

                    <style jsx>{`
                    input:focus{
                        border-color: #66afe9;
                        outline: 0;
                        -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075),0 0 8px rgba(102,175,233,.6);
                        box-shadow: inset 0 1px 1px rgba(0,0,0,.075),0 0 8px rgba(102,175,233,.6)
                    }
                    .check-input{
                        position: relative;
                        width:40px;
                    }
