import Layout from './components/myLayoutAfter';
import Select,{components} from 'react-select';
import React from 'react';
import { typeOptions, occOptions, pairOptions } from './data'
import { Post } from './components/http'
import Alert1 from './components/successAlert'
import './util/config'
import { Tooltip } from './components/Tooltip'


const inputStyle ={
    marginBottom: '60px',
    outlineStyle: 'none' ,
    border: '1px solid #ccc',
    borderRadius: '3px',
    padding: '6px 6px',
    width: '70%',
    fontFamily: "Microsoft soft",
    position:'relative',
    left:"15%"
};

const boxStyle={
    position:'relative',
    left:'20%',
    width:"60%",
    marginBottom:'20px',
    padding:'30px',
    border:'2px solid  #ffd633',
    borderRadius:'2px'

};
const bannerStyle ={
    position:'relative',
    left:'20%',
    textAlign:'center',
    marginTop:"20px",
    width:"60%",
    padding: '10px 10px',
    border:'2px solid  #ffd633',
    backgroundColor:"#ffd633",
    borderRadius:'2px'
};
const labelStyle={   
    position:'relative',
    left:"15%"
};
const groupStyles = {
    border: `2px dotted blue`,
    color: 'white',
    background: 'blue',
    padding: '5px 0px',
    display: 'flex',
  };
  
const GroupHeading = props => (
    <div style={groupStyles}>
      <components.GroupHeading {...props} />  
    </div>
  );

class Relationship extends React.Component{
    constructor(props){
        super(props);
        this.handleSelect = this.handleSelect.bind(this)
    }
    handleSelect(selectOption,name){
        let name1 = name.name
        if(selectOption!==null){
            let option = selectOption.value
            this.props.onChange(name1, option)
        }else{
            let option = ''
            this.props.onChange(name1, option)
        }
        
    }
    render(){
        return(
                <div  style = {{width:'70%',left:'15%',position:'relative',marginBottom:60}}>
                <Select                                   
                    className="basic-single"
                    classNamePrefix="select"
                    isClearable="true"                            
                    isSearchable="true"
                    name= {this.props.name}
                    required ="required"
                    placeholder = {this.props.placeholder}       
                    onChange = {this.handleSelect}
                    options={this.props.options}
                />
                            
                </div>                                                                              
            
        )
    }
}

class contribute extends React.Component{
    static async getInitialProps() {
        var url = global.url
        var method = 'get_all_rel'
    
        const res = await fetch(url+method);
        const data = await res.json();
      
        console.log(data)
      
        return {
            data
        };
    }
    constructor(props){
        super(props);
        this.state={
            type:'',
            submitSuccess: 'none',
            submitSuccess1: 'none',
            moreRe2: 'none',
            moreRe1: 'block',
            date:'',
            occ:'',
            name: '',
            title: '',
            altName: '',
            altNameType: '',
            sDate: '',
            eDate:'none',
            bPlace:'',
            dPlace:'none',
            reId:'none',
            relPair: 'none',
            reId1:'none',
            relPair1: 'none',
            sumNote:'',
            fullNote:'',
            ePrepared: '',
            showMore: 'none',
            exist: false,
            alertNum: 0,
        };
        this.relJson = [],
      
        this.props.data.map((item)=>{
            const value = item.eId
            const label = `${item.eName},${item.sDate},${item.bPlace}`
        
            const rel = {}
            rel.label = label,
            rel.value = value
            this.relJson.push(rel)
        })
        
       
        this.existeId = []
        this.handleChange = this.handleChange.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.handleSelect = this.handleSelect.bind(this);
        this.handleMutiSelect = this.handleMutiSelect.bind(this);
        this.errCheck = this.errCheck.bind(this)
    }
    
   
    handleChange(e){
        let name = e.target.name
        let val = e.target.value
        this.setState({[name]: val});
    }
    handleMutiSelect(selectOption){
        if(selectOption!==null){
            let option = [];
            selectOption.forEach(element => {
                option.push(element.label)
            });
            this.setState({
                occ: option
            })
        }else{
            this.setState({
                occ: ''
            })
        }
       
    }
    handleSelect(name, val){
        this.setState({
            [name] : val
        })
       
    }

    handleClick(e){
        let uId = localStorage.getItem('uId')
        const data = {
            eType:this.state.type,
            eTitle: this.state.eTitle,
            eName : this.state.name,
            alterType: this.state.altNameType,
            alterName: this.state.altName,
            function: this.state.occ,
            sDate: this.state.sDate,
            eDate: this.state.eDate,
            bPlace: this.state.bPlace,
            dPlace: this.state.dPlace,
            sumNote: this.state.sumNote,
            fullNote: this.state.fullNote,
            ePrepared: this.state.ePrepared,
            eId: 'none',
            rId: 'none',
            reId: this.state.reId,
            relPair: this.state.relPair,
            rId1: 'none',
            reId1: this.state.reId1,
            relPair1: this.state.relPair1,
            aId:'none',
            euId : uId, 

        }
        if(e.target.name == "save"){
            Post('save_entity',data).then(res=>{
               
                if(res == 'true'){
                    this.setState({
                        submitSuccess: 'block'
                    })
                }
            })
        }else if(e.target.name =='submit'){
            Post('submit_entity',data).then(res=>{
                if(res == 'true'){
                    this.setState({
                        submitSuccess1: 'block'
                    })
                }
            })
        }
        if(e.target.name=='addAlt'){
            this.setState({
                moreAlt: 'block'
            });
        }
        else if(e.target.name=='deleteAlt'){
            this.setState({
                moreAlt: 'none'
            });
        }else if(e.target.name=='add1'){
            if(this.state.moreRe1 == 'none'){
                this.setState({
                    moreRe1: 'block'
                });
            }else{
                this.setState({
                    moreRe2: 'block'
                });
            } 
        } else if(e.target.name=='delete2'){
            this.setState({
                moreRe2: 'none' 
            });
        }else if(e.target.name=='delete1'){
            this.setState({
                moreRe1: 'none' 
            });
        }else if(e.target.name == "save-basic"){
            let err = this.errCheck("basic")
            if(err ==0){
                const data = {
                    eName:this.state.name,
                    sDate: this.state.sDate,
                    bPlace : this.state.bPlace
                }
                Post("check_basic_eInfo",data).then(res =>{
                    if(res == 'false'){
                        this.setState ({
                            showMore: ' block',
                            exist: false
                        })
                    }else{
                        this.setState ({
                            showMore: ' block',
                            exist: true,
                        }) 
                        res.map(item=>{
                            this.existeId.push(item)
                        })
                    }
                })
            }
        
        }else if(e.target.name == "still-contribute"){
            this.setState({
                exist: false
            })
        }
       
    }
    errCheck(section){
        let date1 = new Date(this.state.sDate);
        let date2 = new Date(this.state.eDate);
        let err = 0
        if(section == "basic"){
            if(date1.getTime()>date2.getTime()){
                alert("check birth date and death date")
                err = 1
            }
        }
        return err
    
    }

    render(){
        return(
            <div>
    
                <Layout />
                
                <div style= {{display: this.state.submitSuccess}}>
                    <Alert1 title="Congradulations!" content="Your draft has been saved!" 
                    button="Ok" url='/' />
                </div>
                <div style= {{display: this.state.submitSuccess1}}>
                    <Alert1 title="Congradulations!" content="Your contribute has been submitted!" 
                    button="Ok" url='/' />
                </div>
            
                <div style={bannerStyle}>
                    <label>Basic Information</label>
                </div> 
                <form>
                    <div style={boxStyle}>
                    <div className="form-group">
                        <Tooltip label="Type" type='dark' tip='Choose a type' id="type" />
                        <Relationship name = "type" placeholder = "select..." onChange={this.handleSelect} options={typeOptions}/>
                    </div>
                        <div className="form-group">
                            <Tooltip label="Name" type='dark' id="name" tip='Type the right name format as:<br />Surname, Firstname, Secondname, Thirdname, etc. in full, no initials' />
                            {this.state.type=='Person'?
                            <input type="text" style = {inputStyle} name= "name" required ="required"
                            placeholder="Surname, Firstname, Secondname, Thirdname, etc. in full, no initials" value={this.state.value} 
                            onChange={this.handleChange}/>:
                            <input type="text" style = {inputStyle} name= "name" required ="required"
                            placeholder="Type full name of entity" value={this.state.value} 
                            onChange={this.handleChange}/>
                            }
                        
                    
                            
                        </div>
                        <div className="form-group">
                            <Tooltip label="Subname of entity" type='dark' tip='Title and honor of entity' id="subname" />
                            <input style = {inputStyle} type="text" name = "eTitle" required ="required"
                            placeholder="Type title of entity" value={this.state.value}
                            onChange={this.handleChange}/>
                        </div>

                        <div className="form-group">
                            <Tooltip label="Alternative Name" type='dark' tip='Previous name of entity' id="altername" />
                            <input style = {inputStyle} type="text" name = "altName" required ="required"
                            placeholder="Type alternative name here" value={this.state.value}
                            onChange={this.handleChange}/> 
                        </div>
                        <div className="form-group">
                            <Tooltip label="Alternative Type" type='dark' tip='Type of previous name of entity. e.g. Person' id="altertype" />
                            <input style = {inputStyle} type="text" name = "altNameType" required ="required"
                            placeholder="Type alternative name type here" value={this.state.value}
                            onChange={this.handleChange}/> 
                        </div>
                        <div className="form-group">
                        {this.state.type=='Person'?
                        <Tooltip label="Birth Date" type='dark' tip='Birth date of entity' id="bDate" />:
                        <Tooltip label="Date from" type='dark' tip='Established date of entity' id="bDate" />}
                        <input type="Date" style = {inputStyle} name= "sDate" required ="required"
                        value={this.state.value} 
                        onChange={this.handleChange}/>
                        
                    </div>
                    <div className="form-group">
                        {this.state.type=='Person'?
                        <Tooltip label="Death Date" type='dark' tip='Death date of entity' id="eDate" />:
                        <Tooltip label="To" type='dark' tip='Last existed date of entity' id="eDate" />}
                        <br/>
                        <input style = {inputStyle} type="Date" name = "eDate" 
                        value={this.state.value} 
                        onChange={this.handleChange}/>
                    </div>
                   
                        {this.state.type=='Person'?
                            <div className="form-group">
                                <Tooltip label="Birth Place" type='dark' tip='Type birth place as:<br />District, City, State, Country. in full, no initials' id="bPlace" />
                                <input style = {inputStyle} type="text" name = "bPlace" 
                                value={this.state.value} required ="required"
                                onChange={this.handleChange}
                                placeholder="District, City, State, Country. in full, no initials"/>
                            </div>:
                            <div className="form-group">
                            <Tooltip label="Place of Existence" type='dark' tip='Type Place of Existence as:<br />District, City, State, Country. in full, no initials' id="bPlace" />
                                <input style = {inputStyle} type="text" name = "bPlace" 
                                value={this.state.value}  required ="required"
                                placeholder ="Place of Existence"
                                onChange={this.handleChange}/>
                            </div>
                        }
                        {
                        this.state.type=='Person'?
                            <div className="form-group">
                            <Tooltip label="Death Place" type='dark' tip='Type death place as:<br />District, City, State, Country. in full, no initials' id="dPlace" />
                                <input style = {inputStyle} type="text" name = "dPlace" 
                                value={this.state.value}
                                onChange={this.handleChange}
                                placeholder="District, City, State, Country. in full, no initials"/>
                            </div>:
                            <div className="form-group">
                            <Tooltip label="Place of Last Existence" type='dark' tip='Type Place of Last Existence as: <br />District, City, State, Country. in full, no initials' id="dPlace" />
                                <input style = {inputStyle} type="text" name = "dPlace" 
                                value={this.state.value} 
                                placeholder ="Place of Last Existence"
                                onChange={this.handleChange}/>
                            </div>
                        }
                        <div style ={{textAlign:"center", marginBottom:'20px'}}>
                            <button className = "btn btn-info" name="save-basic" onClick ={this.handleClick}
                            type="button" style={{borderRadius:"4px"}}>Save</button>
                        </div>

                    </div> 
                   
                   <div style={{display: this.state.showMore,marginTop:'20px'}}>
                        {this.state.exist == true?
                            <div>
                                <label>We already have this entity's basic information. Please check them first:</label><br/>
                                <ul style={{marginBottom: '20px'}}>
                                    {this.existeId.map(item => (
                                        <li key={item.eId}>
                                        <Link href="/../viewEntity/[id]" as={`/../viewEntity/${item.eId}`}>
                                            <a>
                                                {item.eTitle}
                                            </a>
                                        </Link>
                                        </li>
                                    ))}
                                </ul>
                                <button name="still-contirbute" type="button" className="btn btn-info btn-lg"
                                onClick={this.handleClick}>Still want to contribute?</button>
                            </div>:
                            <div>
                            <div style={bannerStyle}>
                                <span>Detail Information</span>
                            </div> 
                            <div style={boxStyle}>
                                <div className ="form-group"  >
                                <Tooltip label="Occupation" type='dark' tip='Choose as many occpuations as you can' id="function" />
                                    <div  style = {{width:'70%',left:'15%',position:'relative',marginBottom:40}}>
                                    <Select                                   
                                        className="basic-single"
                                        classNamePrefix="select"
                                        required ="required"
                                        isClearable="true"                            
                                        isSearchable="true"
                                        isMulti= "true"
                                        name="occupation"
                                        onChange = {this.handleMutiSelect}
                                        options={occOptions}
                                        value={this.state.value}
                                        components={{ GroupHeading }}
                                        styles={{
                                        groupHeading: base => ({
                                            ...base,
                                            flex: '1 1',
                                            color: 'white',
                                            margin: 0,
                                        }),
                                        }}
                                    />
                                
                                    </div>                                                                              
                                </div>
                                     
                                <div className="form-group">
                                    <Tooltip label="Summary Note" type='dark' tip='Type brief life experience of entity' id="sumNote" />
                                    <textarea className ="form-control" rows="5" 
                                    style = {inputStyle} type="text" name = "sumNote" required ="required"
                                    placeholder="Type summary here" value={this.state.value}
                                    onChange={this.handleChange}/>
                                </div>
                                <div className="form-group">
                                <Tooltip label="Full Note" type='dark' tip='Type full life experience of entity' id="fullNote" />
                                    <textarea className ="form-control" rows="5" 
                                    style = {inputStyle} type="text" name = "fullNote" required ="required"
                                    placeholder="Type full note here" value={this.state.value}
                                    onChange={this.handleChange}/>
                                </div>
                            
    
                            </div> 
                            <div style={bannerStyle}>
                                <span>Relationship</span>
                            </div>
                            <div style={boxStyle}>
                                <div style={{display:this.state.moreRe1}}>
                                    <div className ="form-group"  >
                                    <Tooltip label="Related Entity" type='dark' tip='Choose the related entity by name,birth date, and birth place' id="rel1" />
                                        <Relationship name = "reId" placeholder = "select..." onChange={this.handleSelect} options={this.relJson}/>
                                    <Tooltip label="Relationship Pairs" type='dark' tip='The former part of pair represents related entity,<br/>the later one represents this entity' id="rel1" />
                                        <Relationship name = "relPair" placeholder = "select as related entity - entity" onChange={this.handleSelect} options={pairOptions}/> 
                                    </div>
                                </div>
                                <div style ={{textAlign:'center',marginBottom:'20px',marginTop:'20px'}}>
                                    <button name='add1' type="button" className="btn btn-info" style={{borderRadius:"4px"}} onClick={this.handleClick}>Add</button>
                                    <button name='delete1' type="button" className="btn btn-primary" style={{borderRadius:"4px",marginLeft:"50px"}} onClick={this.handleClick}>Delete</button>                                                                           
                                </div>
                            </div>
                      
                     
                            <div style={{display: this.state.moreRe2}}>
                                <div style={boxStyle}>
                                <div className ="form-group"  >
                                    <label style={labelStyle}>Related Entity</label><br/>
                                    <Relationship name = "reId1" placeholder = "select..." onChange={this.handleSelect} options={this.relJson}/>
                                    <label style={labelStyle}>Relationship</label><br/>
                                    <Relationship name = "relPair1" placeholder = "select as related entity - entity" onChange={this.handleSelect} options={pairOptions}/> 
                                <div style ={{textAlign:'center',marginBottom:'20px'}}>
                                    <button name='add2' type="button" className="btn btn-info" style={{borderRadius:"4px"}} onClick={this.handleClick}>Add</button>
                                    <button name='delete2' type="button" className="btn btn-primary" style={{borderRadius:"4px",marginLeft:"50px"}} onClick={this.handleClick}>Delete</button>                                                                           
                                </div>
                                </div>
                                </div>
                            </div>
    
                            <div>
                                <div className="form-group">
                                    <label style={labelStyle}>ePrepared by</label><br/>
                                    <input style = {inputStyle} type="text" name = "ePrepared" 
                                    placeholder="Type entity ePrepared by..." value={this.state.value}
                                    onChange={this.handleChange}/>
                                </div>
                                <div style ={{textAlign:'center',marginBottom:'20px'}}>
                                <button type="button" name="submit" className="btn btn-info btn-lg" onClick={this.handleClick} style={{borderRadius:"4px",marginTop:"20px"}}>Send entity</button>
                                <button type="button" name="save" className="btn btn-danger btn-lg" onClick={this.handleClick} style={{borderRadius:"4px",marginTop:"20px",marginLeft:"50px"}}>Save entity</button>
                                </div>
                             
                            </div>
                   
                        </div>}
                   </div> 
                   

                    <style jsx>{`
                    input:focus{
                        border-color: #000;
                        outline: 0;
                        -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075),0 0 8px rgba(102,175,233,.6);
                        box-shadow: inset 0 1px 1px rgba(0,0,0,.075),0 0 8px rgba(102,175,233,.6)
                    }
                `}</style>
                </form>
            </div>
                       

        );
        
    }
} 

export default contribute;
