import Layout from "./components/myLayoutBefore";
import bcrypt from "bcryptjs";
import Router from 'next/router';
import {Alert} from './components/warnAlert';
import { Post } from "./components/http";

const boxStyle={
    position:'relative',
    left:'20%',
    textAlign:'center',
    width:"60%",
    marginBottom:'20px',
    padding:'30px',
    border:'2px solid  #ffd633',
    borderRadius:'2px',
    
};   
const bannerStyle ={
    position:'relative',
    left:'20%',
    textAlign:'center',
    marginTop:"20px",
    width:"60%",
    padding: '10px 10px',
    border:'2px solid  #ffd633',
    backgroundColor:"#ffd633",
    borderRadius:'2px',
};

const inputStyle ={
    margin: 30,
    outlineStyle: 'none' ,
    border: '1px solid #ccc',
    borderRadius: '3px',
    padding: '10px 10px',
    width: '70%',
    fontSize: '24px',
    fontFamily: "Microsoft soft",

};
var salt = bcrypt.genSaltSync(10);
class signin extends React.Component{
    constructor(props) {
        super(props);
        this.state = {email: '',
                    password: '',
                    showAlert: 'none',
                    err: '',
                    data:''
                };
      
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleClose = this.handleClose.bind(this)
      }
    
    handleChange(event) {
        let name = event.target.name;
        let val = event.target.value;
        this.setState({[name]: val});
       
    }
    handleClose(){
        this.setState({
            showAlert:'none'
        })
    }
    handleSubmit(event) {
        event.preventDefault();
        var psw = bcrypt.hashSync(this.state.password, salt);

        var data ={
            email:this.state.email,
            password:psw
        }
      
        Post('signin', data).then(res=>{
            console.log(res)
            if(res=='-1'|| res == '-2'){
                this.setState({showAlert: ' block'})
                this.setState({err: res})
            }else{
                const same = bcrypt.compareSync(this.state.email,res.psw)
                console.log(same)
                if(same){
                    localStorage.setItem('uId',res.uId);
                    localStorage.setItem('role',res.role);
                    Router.push({
                        pathname:'/',
                        query: res
                    })
                }else{
                    this.setState({showAlert: ' block'})
                } 
            }
        })
       
    
    }
        
      render(){
        return(
            <div>
                <Layout/>
                <div style={{display: this.state.showAlert}}>
                    {this.state.err=='-1'?<Alert title ='warning!' content = 'Invalid email or password!' onClick={this.handleClose}/>:
                    <Alert title ='warning!' content = 'Your application has not been approved! Please wait.' onClick={this.handleClose}/>}
                </div>
                <div style={bannerStyle}>
                    <span>Sign in</span>
                </div>
                <form onSubmit={this.handleSubmit} style={{textAlign: 'center'}}>
                    <div style={boxStyle}>
                    <input style = {inputStyle} required="required" type="email" name= "email" placeholder="Enter your email" value={this.state.value} onChange={this.handleChange}/><br/>
                    <input style = {inputStyle} required="required" type="password" name = "password" placeholder="Enter your password" value={this.state.value} onChange={this.handleChange}/><br/>
                   
                    <button type="submit" className="btn btn-primary btn-lg" style={{borderRadius:"4px",marginTop:"20px"}}>Sign in</button>
                    <style jsx>{`
                        input:focus{
                            border-color: #66afe9;
                            outline: 0;
                            -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075),0 0 8px rgba(102,175,233,.6);
                            box-shadow: inset 0 1px 1px rgba(0,0,0,.075),0 0 8px rgba(102,175,233,.6)
                        }
                    `}</style>
                    </div>
                </form> 
                <div style={{textAlign:'center'}}>
                        <span style={{color:'red'}}>have no account?&nbsp;&nbsp;</span>
                        <a href='/signup'>sign up</a>
                </div>
                
            </div> 
        );
    }

}
export default signin;
