import Layout from './components/myLayoutAfter';
import Select,{components} from 'react-select';
import React from 'react';
import { typeOptions, occOptions, pairOptions } from './data'
import { Post } from './components/http'
import Alert1 from './components/successAlert'
import './util/config'
import { Tooltip } from './components/Tooltip'


const inputStyle ={
    marginBottom: '60px',
    outlineStyle: 'none' ,
    border: '1px solid #ccc',
    borderRadius: '3px',
    padding: '6px 6px',
    width: '70%',
    fontFamily: "Microsoft soft",
    position:'relative',
    left:"15%"
};

const boxStyle={
    position:'relative',
    left:'20%',
    width:"60%",
    marginBottom:'20px',
    padding:'30px',
    border:'2px solid  #ffd633',
    borderRadius:'2px'

};
const bannerStyle ={
    position:'relative',
    left:'20%',
    textAlign:'center',
    marginTop:"20px",
    width:"60%",
    padding: '10px 10px',
    border:'2px solid  #ffd633',
    backgroundColor:"#ffd633",
    borderRadius:'2px'
};
const labelStyle={   
    position:'relative',
    left:"15%"
};
const groupStyles = {
    border: `2px dotted blue`,
    color: 'white',
    background: 'blue',
    padding: '5px 0px',
    display: 'flex',
  };
  
const GroupHeading = props => (
    <div style={groupStyles}>
      <components.GroupHeading {...props} />  
    </div>
  );

class Relationship extends React.Component{
    constructor(props){
        super(props);
        this.handleSelect = this.handleSelect.bind(this)
    }
    handleSelect(selectOption,name){
        let name1 = name.name
        if(selectOption!==null){
            let option = selectOption.value
            this.props.onChange(name1, option)
        }else{
            let option = ''
            this.props.onChange(name1, option)
        }
        
    }
    render(){
        return(
                <div  style = {{width:'70%',left:'15%',position:'relative',marginBottom:60}}>
                <Select                                   
                    className="basic-single"
                    classNamePrefix="select"
                    isClearable="true"                            
                    isSearchable="true"
                    name= {this.props.name}
                    required ="required"
                    placeholder = {this.props.placeholder}       
                    onChange = {this.handleSelect}
                    options={this.props.options}
                />
                            
                </div>                                                                              
            
        )
    }
}

class contribute extends React.Component{
    constructor(props){
        super(props);
        this.state={
            type:'',
            submitSuccess: 'none',
            submitSuccess1: 'none',
            moreRe2: 'none',
            moreRe1: 'block',
            date:'',
            occ:'',
            name: '',
            title: '',
            altName: '',
            altNameType: '',
            sDate: '',
            eDate:'none',
            bPlace:'',
            dPlace:'none',
            reId:'none',
            relPair: 'none',
            reId1:'none',
            relPair1: 'none',
            sumNote:'',
            fullNote:'',
            ePrepared: '',
            showMore: 'none',
            exist: false,
            alertNum: 0,
        };
        this.relJson = [],
      
        this.props.data.map((item)=>{
            const value = item.eId
            const label = `${item.eName},${item.sDate},${item.bPlace}`
        
            const rel = {}
            rel.label = label,
            rel.value = value
            this.relJson.push(rel)
        })
        
       
        this.existeId = []
        this.handleChange = this.handleChange.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.handleSelect = this.handleSelect.bind(this);
        this.handleMutiSelect = this.handleMutiSelect.bind(this);
        this.errCheck = this.errCheck.bind(this)
    }
    
   
    handleChange(e){
        let name = e.target.name
        let val = e.target.value
        this.setState({[name]: val});
    }
    handleMutiSelect(selectOption){
        if(selectOption!==null){
            let option = [];
            selectOption.forEach(element => {
                option.push(element.label)
            });
            this.setState({
                occ: option
            })
        }else{
            this.setState({
                occ: ''
            })
        }
       
    }
    handleSelect(name, val){
        this.setState({
            [name] : val
        })
       
    }

    handleClick(e){
        let uId = localStorage.getItem('uId')
        const data = {
            eType:this.state.type,
            eTitle: this.state.eTitle,
            eName : this.state.name,
            alterType: this.state.altNameType,
            alterName: this.state.altName,
            function: this.state.occ,
            sDate: this.state.sDate,
            eDate: this.state.eDate,
            bPlace: this.state.bPlace,
            dPlace: this.state.dPlace,
            sumNote: this.state.sumNote,
            fullNote: this.state.fullNote,
            ePrepared: this.state.ePrepared,
            eId: 'none',
            rId: 'none',
            reId: this.state.reId,
            relPair: this.state.relPair,
            rId1: 'none',
            reId1: this.state.reId1,
            relPair1: this.state.relPair1,
            aId:'none',
            euId : uId, 

        }
        if(e.target.name == "save"){
            Post('save_entity',data).then(res=>{
               
                if(res == 'true'){
                    this.setState({
                        submitSuccess: 'block'
                    })
                }
            })
        }else if(e.target.name =='submit'){
            Post('submit_entity',data).then(res=>{
                if(res == 'true'){
                    this.setState({
                        submitSuccess1: 'block'
                    })
                }
            })
        }
        if(e.target.name=='addAlt'){
            this.setState({
                moreAlt: 'block'
            });
        }
        else if(e.target.name=='deleteAlt'){
            this.setState({
                moreAlt: 'none'
            });
        }else if(e.target.name=='add1'){
            if(this.state.moreRe1 == 'none'){
                this.setState({
                    moreRe1: 'block'
                });
            }else{
                this.setState({
                    moreRe2: 'block'
                });
            } 
        } else if(e.target.name=='delete2'){
            this.setState({
                moreRe2: 'none' 
            });
        }else if(e.target.name=='delete1'){
            this.setState({
                moreRe1: 'none' 
            });
        }else if(e.target.name == "save-basic"){
            let err = this.errCheck("basic")
            if(err ==0){
                const data = {
                    eName:this.state.name,
                    sDate: this.state.sDate,
                    bPlace : this.state.bPlace
                }
                Post("check_basic_eInfo",data).then(res =>{
                    if(res == 'false'){
                        this.setState ({
                            showMore: ' block',
                            exist: false
                        })
                    }else{
                        this.setState ({
                            showMore: ' block',
                            exist: true,
                        }) 
                        res.map(item=>{
                            this.existeId.push(item)
                        })
                    }
                })
            }
        
        }else if(e.target.name == "still-contribute"){
            this.setState({
                exist: false
            })
        }
       
    }
    errCheck(section){
        let date1 = new Date(this.state.sDate);
        let date2 = new Date(this.state.eDate);
        let err = 0
        if(section == "basic"){
            if(date1.getTime()>date2.getTime()){
                alert("check birth date and death date")
                err = 1
            }
        }
        return err
    
    }

    render(){
        return(
            <div>
    
                <Layout />
                
                <div style= {{display: this.state.submitSuccess}}>
                    <Alert1 title="Congradulations!" content="Your draft has been saved!" 
                    button="Ok" url='/' />
                </div>
                <div style= {{display: this.state.submitSuccess1}}>
                    <Alert1 title="Congradulations!" content="Your contribute has been submitted!" 
                    button="Ok" url='/' />
                </div>
            
                <div style={bannerStyle}>
                    <label>Basic Information</label>
                </div> 
                <form>
                    <div style={boxStyle}>
                    <div className="form-group">
                        <Tooltip label="Type" type='dark' tip='Choose a type' id="type" />
                        <Relationship name = "type" placeholder = "select..." onChange={this.handleSelect} options={typeOptions}/>
                    </div>
                        <div className="form-group">
                            <Tooltip label="Name" type='dark' id="name" tip='Type the right name format as:<br />Surname, Firstname, Secondname, Thirdname, etc. in full, no initials' />
                            {this.state.type=='Person'?
                            <input type="text" style = {inputStyle} name= "name" required ="required"
