                                        className="basic-single"
                                        classNamePrefix="select"
                                        required ="required"
                                        isClearable="true"                            
                                        isSearchable="true"
                                        isMulti= "true"
                                        name="occupation"
                                        onChange = {this.handleMutiSelect}
                                        options={occOptions}
                                        value={this.state.value}
                                        components={{ GroupHeading }}
                                        styles={{
                                        groupHeading: base => ({
                                            ...base,
                                            flex: '1 1',
                                            color: 'white',
                                            margin: 0,
                                        }),
                                        }}
                                    />
                                
                                    </div>                                                                              
                                </div>
                                     
                                <div className="form-group">
                                    <Tooltip label="Summary Note" type='dark' tip='Type brief life experience of entity' id="sumNote" />
                                    <textarea className ="form-control" rows="5" 
                                    style = {inputStyle} type="text" name = "sumNote" required ="required"
                                    placeholder="Type summary here" value={this.state.value}
                                    onChange={this.handleChange}/>
                                </div>
                                <div className="form-group">
                                <Tooltip label="Full Note" type='dark' tip='Type full life experience of entity' id="fullNote" />
                                    <textarea className ="form-control" rows="5" 
                                    style = {inputStyle} type="text" name = "fullNote" required ="required"
                                    placeholder="Type full note here" value={this.state.value}
                                    onChange={this.handleChange}/>
                                </div>
                            
    
                            </div> 
                            <div style={bannerStyle}>
                                <span>Relationship</span>
                            </div>
                            <div style={boxStyle}>
                                <div style={{display:this.state.moreRe1}}>
                                    <div className ="form-group"  >
                                    <Tooltip label="Related Entity" type='dark' tip='Choose the related entity by name,birth date, and birth place' id="rel1" />
                                        <Relationship name = "reId" placeholder = "select..." onChange={this.handleSelect} options={this.relJson}/>
                                    <Tooltip label="Relationship Pairs" type='dark' tip='The former part of pair represents related entity,<br/>the later one represents this entity' id="rel1" />
                                        <Relationship name = "relPair" placeholder = "select as related entity - entity" onChange={this.handleSelect} options={pairOptions}/> 
                                    </div>
                                </div>
                                <div style ={{textAlign:'center',marginBottom:'20px',marginTop:'20px'}}>
                                    <button name='add1' type="button" className="btn btn-info" style={{borderRadius:"4px"}} onClick={this.handleClick}>Add</button>
                                    <button name='delete1' type="button" className="btn btn-primary" style={{borderRadius:"4px",marginLeft:"50px"}} onClick={this.handleClick}>Delete</button>                                                                           
                                </div>
                            </div>
                      
                     
                            <div style={{display: this.state.moreRe2}}>
                                <div style={boxStyle}>
                                <div className ="form-group"  >
                                    <label style={labelStyle}>Related Entity</label><br/>
                                    <Relationship name = "reId1" placeholder = "select..." onChange={this.handleSelect} options={this.relJson}/>
                                    <label style={labelStyle}>Relationship</label><br/>
                                    <Relationship name = "relPair1" placeholder = "select as related entity - entity" onChange={this.handleSelect} options={pairOptions}/> 
                                <div style ={{textAlign:'center',marginBottom:'20px'}}>
                                    <button name='add2' type="button" className="btn btn-info" style={{borderRadius:"4px"}} onClick={this.handleClick}>Add</button>
                                    <button name='delete2' type="button" className="btn btn-primary" style={{borderRadius:"4px",marginLeft:"50px"}} onClick={this.handleClick}>Delete</button>                                                                           
                                </div>
                                </div>
                                </div>
                            </div>
    
                            <div>
                                <div className="form-group">
                                    <label style={labelStyle}>ePrepared by</label><br/>
                                    <input style = {inputStyle} type="text" name = "ePrepared" 
                                    placeholder="Type entity ePrepared by..." value={this.state.value}
                                    onChange={this.handleChange}/>
                                </div>
                                <div style ={{textAlign:'center',marginBottom:'20px'}}>
                                <button type="button" name="submit" className="btn btn-info btn-lg" onClick={this.handleClick} style={{borderRadius:"4px",marginTop:"20px"}}>Send entity</button>
                                <button type="button" name="save" className="btn btn-danger btn-lg" onClick={this.handleClick} style={{borderRadius:"4px",marginTop:"20px",marginLeft:"50px"}}>Save entity</button>
                                </div>
                             
                            </div>
                   
                        </div>}
                   </div> 
                   

                    <style jsx>{`
                    input:focus{
                        border-color: #000;
                        outline: 0;
